import React from 'react';
import { NavLink } from "react-router-dom";

const handleLogout = () => {
    // Remove the JWT token from localStorage
    localStorage.removeItem("authToken");

    // Optionally, redirect to the login page
    alert("You have been logged out.");
};


const Navbar = () => {
    const activeState = ({ isActive }) => {
        return {
          color: isActive ? "#82060D" : "",
          fontWeight: isActive ? "bold" : ""
        };
      };
    return (
        /*  Search bar
            Profile icon
            Notification?
            Messenger?
        */
            /*
        <nav className="navbar">
                <ul className="nav-links">
                    <NavLink to="/create" style={activeState}><li><a>+ Create</a></li></NavLink>
                    <NavLink to="/" style={activeState}><li><a>Notification</a></li></NavLink>
                    <NavLink to="/" style={activeState}><li><a>Message</a></li></NavLink>
                    <NavLink to="/profile" style={activeState}><li><a>Profile</a></li></NavLink>
                </ul>
        </nav>
        */
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
        <a class="navbar-brand" href="#">Navbar</a>
        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
      
        <div class="collapse navbar-collapse" id="navbarTogglerDemo02">
            <ul class="navbar-nav mr-auto mt-2 mt-lg-0">
                <li class="nav-item active">
                <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                </li>
                <li class="nav-item">
                <a class="nav-link" href="#">Link</a>
                </li>
                <li class="nav-item">
                <a class="nav-link disabled" href="#">Disabled</a>
                </li>
            </ul>
            <form class="form-inline my-2 my-lg-0">
                <input class="form-control mr-sm-2" type="search" placeholder="Search" />
                <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
            </form>
            </div>
        </nav>
    )
}


export default Navbar;
