import React from 'react';
import { NavLink } from "react-router-dom";

const handleLogout = () => {
    // Remove the JWT token from localStorage
    localStorage.removeItem("authToken");

    // Optionally, redirect to the login page
    alert("You have been logged out.");
};


const Navbar = () => {
    const activeState = ({ isActive }) => {
        return {
          color: isActive ? "#82060D" : "",
          fontWeight: isActive ? "bold" : ""
        };
      };
    return (
        /*  Search bar
            Profile icon
            Notification?
            Messenger?
        */
            /*
        <nav className="navbar">
                <ul className="nav-links">
                    <NavLink to="/create" style={activeState}><li><a>+ Create</a></li></NavLink>
                    <NavLink to="/" style={activeState}><li><a>Notification</a></li></NavLink>
                    <NavLink to="/" style={activeState}><li><a>Message</a></li></NavLink>
                    <NavLink to="/profile" style={activeState}><li><a>Profile</a></li></NavLink>
                </ul>
        </nav>
        */
       <nav class="navbar navbar-expand navbar-light bg-light">
        <ul class="nav navbar-nav">
            <li class="nav-item">
                <a class="nav-link active" href="#" aria-current="page"
                    >Nav 1 <span class="visually-hidden">(current)</span></a
                >
            </li>
            <li class="nav-item">
                <a class="nav-link" href="#">Nav 2</a>
            </li>
        </ul>
       </nav>
       
       
    )
}


export default Navbar;
