import React from 'react';
import { NavLink } from "react-router-dom";

const handleLogout = () => {
    // Remove the JWT token from localStorage
    localStorage.removeItem("authToken");

    // Optionally, redirect to the login page
    alert("You have been logged out.");
};


const Navbar = () => {
    const activeState = ({ isActive }) => {
        return {
          color: isActive ? "#82060D" : "",
          fontWeight: isActive ? "bold" : ""
        };
      };
    return (
        /*  Search bar
            Profile icon
            Notification?
            Messenger?
        */
            /*
        <nav className="navbar">
                <ul className="nav-links">
                    <NavLink to="/create" style={activeState}><li><a>+ Create</a></li></NavLink>
                    <NavLink to="/" style={activeState}><li><a>Notification</a></li></NavLink>
                    <NavLink to="/" style={activeState}><li><a>Message</a></li></NavLink>
                    <NavLink to="/profile" style={activeState}><li><a>Profile</a></li></NavLink>
                </ul>
        </nav>
        */
       <ul
        class="nav nav-tabs"
       >
        <li class="nav-item">
            <a href="#" class="nav-link active" aria-current="page"
                >Active</a
            >
        </li>
        <li class="nav-item dropdown">
            <a
                class="nav-link dropdown-toggle"
                data-bs-toggle="dropdown"
                href="#"
                role="button"
                aria-haspopup="true"
                aria-expanded="false"
                >Dropdown</a
            >
            <div class="dropdown-menu">
                <a class="dropdown-item" href="#">Action</a>
                <a class="dropdown-item" href="#">Another action</a>
                <div class="dropdown-divider"></div>
                <a class="dropdown-item" href="#">Action</a>
            </div>
        </li>
        <li class="nav-item">
            <a href="#" class="nav-link">Another link</a>
        </li>
        <li class="nav-item">
            <a href="#" class="nav-link disabled">Disabled</a>
        </li>
       </ul>
       
       
    )
}


export default Navbar;
